import React from 'react';

//app
import styles from './LossActionTable.styles';
import { TableCell, Status, Translate, Chip, Link } from 'components';
import * as utils from 'utils';
import config from 'config';

//mui
import { makeStyles, TableRow, Typography, Box } from '@material-ui/core';

export function LossActionTableRowView({ data, columnProps}) {
  const classes = makeStyles(styles, { name: 'LossActionTable' })();

  return (
    <>
      <TableRow>
        <td colspan="6">
        <Box >
            <Typography variant="h2">
              Claim UA13798A01
            </Typography>
            <Typography variant="p">
              International Energy - Plant, Machinery and Equipment - Terraseis 1040 Limited
            </Typography>

        </Box>
        </td>
      </TableRow>
      {data.claimsItems.map((item, idx) => 
        <TableRow key={item.claimID} data-testid={`search-row-${item.actionId}`} hover>
          <TableCell {...columnProps('actionId')} data-testid={`row-col-${item.actionId}`}>
            <Link text={item.actionId} color="secondary" onClick={() => {}} />
          </TableCell>
          <TableCell {...columnProps('name')} data-testid={`row-col-${item.name}`}>
            <Chip
              label={item.name}
              size="small"
              type="error"
              color='primary'
              variant="outlined"
            />
          </TableCell>
          <TableCell {...columnProps('description')} data-testid={`row-col-${item.description}`}>
            {item.description}
          </TableCell>
          <TableCell {...columnProps('team')} data-testid={`row-col-${item.team}`}>
            {item.team}
          </TableCell>
          <TableCell {...columnProps('assignedTo')} data-testid={`row-col-${item.assignedTo}`}>
            {item.assignedTo}
          </TableCell>
          <TableCell {...columnProps('createdDate')} data-testid={`row-col-${item.createdDate}`}>
            {utils.string.t('format.date', {
              value: { date: item?.createdDate, format: config.ui.format.date.textTime },
            })}
          </TableCell>
          <TableCell {...columnProps('targetDate')} data-testid={`row-col-${item.targetDate}`}>
            <Typography variant="body2" color="error">
              {utils.string.t('format.date', {
                value: { date: item?.targetDate, format: config.ui.format.date.text },
              })}
            </Typography>
          </TableCell>
          <TableCell {...columnProps('priority')} data-testid={`row-col-${item.priority}`}>
            {item.priority}
          </TableCell>
          <TableCell {...columnProps('status')} data-testid={`row-col-${item.status}`}>
            {item?.status && <Status size="sm" text={<Translate label={item?.status} />} status={item?.status === "Completed" ? 'success' : 'error'} />}
          </TableCell>
          <TableCell {...columnProps('insured')} data-testid={`row-col-${item.insured}`}>
            {item.insured}
          </TableCell>
          <TableCell {...columnProps('division')} data-testid={`row-col-${item.division}`}>
            {item.division}
          </TableCell>
        </TableRow>
      )}
    </>
  );
}
