import React from 'react';

// app
import * as utils from 'utils';
import { LossActionTable } from 'modules';
import { TableFilters, TableToolbar } from 'components';
import styles from './LossAction.styles';
import { useMedia } from 'hooks';

// mui
import { makeStyles, Box } from '@material-ui/core';

LossActionView.propTypes = {};

export function LossActionView({
  items,
  sort,
  columnsArray,
  columnProps,
  viewFields,
  tableFilterFields,
  isFetchingFilters,
  control,
  handlers,
  searchTerm,
}) {
  const media = useMedia();
  const classes = makeStyles(styles, { name: 'LossAction' })({ isMobile: media.mobile });

  return (
    <Box mt={3} data-testid="loss-action">
      <TableToolbar>
        <TableFilters
          search
          searchPlaceholder={utils.string.t('claims.processing.searchBarPlaceholder')}
          searchMinChars={3}
          nestedClasses={{ searchMaxWidth: classes.searchMaxWidth }}
          filtersArray={tableFilterFields}
          isFetchingFilters={isFetchingFilters}
          handlers={{
            onSearch: handlers.search,
            onResetFilter: handlers.resetFilter,
            onFilter: (values) => handlers.searchFilter(values),
            onToggleColumn: handlers.toggleColumn,
            onResetSearch: handlers.resetSearch,
          }}
          searchTerm={searchTerm}
        />
      </TableToolbar>
      <LossActionTable
        lossActions={items}
        cols={columnsArray}
        columnProps={columnProps}
      />
    </Box>
  );
}
