import React from 'react';
import { useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import { useHistory, useLocation } from 'react-router';

// app
import styles from './TableRowHeader.styles';
import { TableCell, Button } from 'components';
import { STATUS_CLAIMS_DRAFT } from 'consts';
import { setClaimsStepperControl } from 'stores';
import config from 'config';

// mui
import { makeStyles, Box, TableRow, Typography } from '@material-ui/core';
import OpenInNewOutlinedIcon from '@material-ui/icons/OpenInNewOutlined';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

TableRowHeaderView.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string.isRequired,
  expanded: PropTypes.bool,
  children: PropTypes.oneOfType([PropTypes.arrayOf(PropTypes.node), PropTypes.node]).isRequired,
  setIsOpen: PropTypes.func,
  claimInformation: PropTypes.object,
};

export function TableRowHeaderView({ title, subtitle, isOpen, onClick, children, color, claimInformation }) {
  const classes = makeStyles(styles, { name: 'TableRowHeader' })({ expanded: isOpen });
  const dispatch = useDispatch();
  const history = useHistory();
  const location = useLocation();

  const handleEditClaim = (claim) => {
    console.log('claim ', claim.actionChildItemList[0]);
    /*  dispatch(setClaimsStepperControl(1));
    history.push({
      pathname: `${config.routes.claimsFNOL.newLoss}`,
      state: {
        linkPolicy: {
          isSearchTerm: true,
          claimObj: claim,
        },
        redirectUrl: location?.pathname,
      },
    }); */
  };

  return (
    <>
      <TableRow className={classes.wrapper} onClick={onClick} data-testid="table-row-header" style={{ background: color }}>
        <TableCell colSpan="100">
          <Box className={classes.panel}>
            <KeyboardArrowDownIcon className={classes.arrow} />
            <Typography variant="h2" className={classes.title}>
              {title}
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {subtitle}
            </Typography>
            <Button
              icon={OpenInNewOutlinedIcon}
              color="default"
              size="medium"
              variant="outlined"
              style={{ border: 'none' }}
              nestedClasses={{ btn: classes.button }}
              onClick={() => handleEditClaim(claimInformation)}
              disabled={claimInformation?.claimStatus === STATUS_CLAIMS_DRAFT}
            />
          </Box>
        </TableCell>
      </TableRow>
      {children}
    </>
  );
}
