import React from 'react';
import PropTypes from 'prop-types';

//app
import { Breadcrumb, Layout, Tabs, PopoverMenu, SectionHeader } from 'components';
import { ClaimsAcknowledgement, ClaimRefTasks, ClaimRefRfis, ClaimRefNotes, ClaimRefAuditTrail, ClaimsUploadViewSearchDocs } from 'modules';
import * as utils from 'utils';
import * as constants from 'consts';

//mui
import { Divider } from '@material-ui/core';
import DescriptionIcon from '@material-ui/icons/Description';

ClaimDashboardView.propTypes = {
  tabs: PropTypes.array.isRequired,
  selectedTab: PropTypes.string.isRequired,
  handleSelectTab: PropTypes.func,
  popoverItems: PropTypes.array.isRequired,
  claimObj: PropTypes.object.isRequired,
  claimRefFromLossObj: PropTypes.shape({
    claimID: PropTypes.string,
    claimRef: PropTypes.string,
    lossRef: PropTypes.string,
    sourceId: PropTypes.string,
    policyId: PropTypes.string,
  }),
  breadcrumbs: PropTypes.arrayOf(
    PropTypes.shape({
      name: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      link: PropTypes.string.isRequired,
    })
  ).isRequired,
  setCheckPage: PropTypes.func,
  checkRedirectLocation: PropTypes.bool.isRequired,
};

export function ClaimDashboardView({
  tabs,
  selectedTab,
  handleSelectTab,
  popoverItems,
  claimObj,
  breadcrumbs,
  setCheckPage,
  checkRedirectLocation,
  claimRefFromLossObj,
}) {
  return (
    <>
      <Breadcrumb links={breadcrumbs} />
      <Divider />
      <Layout testid="ClaimDashboard">
        <Layout main>
          <SectionHeader
            title={utils.string.t('claims.claimRef.text', {
              claimRef: !checkRedirectLocation ? claimObj?.claimRef : claimRefFromLossObj.claimRef,
            })}
            icon={DescriptionIcon}
            testid="claim-ref-header"
          >
            <PopoverMenu
              variant="outlined"
              id="claims-functions"
              size="small"
              color="primary"
              text={utils.string.t('claims.claimRef.popOverFunction')}
              isButton
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              // items={popoverItems}
            />
          </SectionHeader>

          <Tabs tabs={tabs} value={selectedTab} onChange={(tabName) => handleSelectTab(tabName)} />
          {selectedTab === 'claimRefDetail' && (
            <ClaimsAcknowledgement claimDataFromRef={!checkRedirectLocation ? claimObj : claimRefFromLossObj} />
          )}
        </Layout>
      </Layout>
    </>
  );
}
