import * as utils from 'utils';
import types from './types';

export const fetchDocumentMetaData = (selectedSourceId, metaDataSectionRef) => (dispatch, getState) => {
  // prettier-ignore
  const { user: { auth }, config: { vars: { endpoint }, }, ui: { dmsContext }, } = getState();

  // eslint-disable-next-line
  const { sectionType, sourceId, divisionId, sectionRef, referenceId } = utils.dmsFormatter.getMetaDataParams(
    getState(),
    dmsContext,
    selectedSourceId,
    metaDataSectionRef
  );

  const params = {
    sectionType, // corresponds to contexts: Loss, Claims, Tasks, Case
    sourceId, // is non-mandatory for Loss context
    ...(divisionId ? { divisionId } : {}), // is needed for Claims and Loss contexts
    referenceId, // corresponds to ClaimId, lossDetailID, policyId etc
    sectionRef, // corresponds to ClaimRef, lossDetailRef, policyRef etc
  };

  dispatch({ type: types.GET_DOCUMENT_META_DATA_PENDING });

  return utils.api
    .get({
      token: auth.accessToken,
      endpoint: endpoint.dmsService,
      path: 'data/context/details',
      params,
    })
    .then((response) => utils.api.handleResponse(response))
    .then((json) => utils.api.handleNewData(json))
    .then(({ data }) => {
      return dispatch({
        type: types.GET_DOCUMENT_META_DATA_SUCCESS,
        payload: data,
      });
    })
    .catch((err) => {
      const defaultError = {
        file: 'stores/dms.actions.getMetaData',
      };

      utils.api.handleError(err, defaultError);
      return dispatch({
        type: types.GET_DOCUMENT_META_DATA_REJECTED,
        payload: err,
      });
    });
};
