import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

//app
import * as utils from 'utils';

import { Accordion, FormAutocompleteMui, FormDate, FormLabel, FormGrid, FormText, Layout } from 'components';
import { ClaimsUnderwritingGroups, ClaimsMovementType } from 'modules';
import styles from './LinkClaimPolicy.styles';

import { makeStyles, Grid, Box, Typography } from '@material-ui/core';

export function LinkClaimPolicyView({
    fields, 
    policyData, 
    uwResetKey, 
    hasClaimRef, 
    underWritingGroups, 
    isBordereauFlag,
    validation, 
    setValidation,
    index,
    isFormsEdited,
    setFormEditedStatus,
    postSaveHandler,
    postEditSaveHandler,
    saveStatus,
    selectedPolicyRender,
    handleFormStatus,
}){
    const classes = makeStyles(styles, { name: 'EnterClaimInformation' })();
    const [isEdit, setIsEdit] = useState(false);
    const [initialLoad, setInitialLoad] = useState(false);
    const [accExpandStatus, setAccExpandStatus] = useState(true);
    const defaultValues = utils.form.getInitialValues(fields);
    const validationSchema = utils.form.getValidationSchema(fields);

    const claimForm = useForm({
        defaultValues,
        ...(validationSchema && { resolver: yupResolver(validationSchema), context: { validation } }),
    });
    const { handleSubmit, setValue, control, errors, watch } = claimForm;

    const insuredWatch = Boolean(watch('insured'));
    const clientWatch = Boolean(watch('client'));
    const countryCodeWatch = Boolean(watch('countryCode'));
    const certificateNumberWatch = Boolean(watch('certificateNumber'));
    const certificateInceptionDateWatch = Boolean(watch('certificateInceptionDate'));
    const certificateExpiryDateWatch = Boolean(watch('certificateExpiryDate'));
    const originalCurrencyCodeWatch = Boolean(watch ('originalCurrencyCode'));
    const settlementCurrencyCodeWatch = Boolean(watch('settlementCurrencyCode'));
    const interestWatch = Boolean(watch('interest'));
    const underWritingGroupsWatch = Boolean(watch('underWritingGroups'));

    const formWatcher = () => {
        return insuredWatch || clientWatch || countryCodeWatch || certificateNumberWatch || certificateInceptionDateWatch || certificateExpiryDateWatch || originalCurrencyCodeWatch || settlementCurrencyCodeWatch || interestWatch || underWritingGroupsWatch;
    }

    const saveHandler = () => {
        if((selectedPolicyRender!=='' && initialLoad) || (initialLoad && validation) ){
            handleFormStatus();
        }
    };

    if(formWatcher() !== isEdit){
        setIsEdit(true);
    }

    useEffect(() => {
        if(isEdit && initialLoad){
            saveHandler();
        } else {
            const formState = isFormsEdited?.map((form) => ({ ...form }));
            if(formState[index]['formEditedStatus']){
                formState[index]['formEditedStatus'] = false;
                formState[index]['isSubmitted'] = true;
                setFormEditedStatus(formState);
            }
            setInitialLoad(true);
        }   
    }, [isEdit]); // eslint-disable-line react-hooks/exhaustive-deps

    useEffect(()=>{
        if(saveStatus && !isFormsEdited[index]?.formEditedStatus && initialLoad){
            handleSubmit(postSaveHandler)();
        }else if(saveStatus && isFormsEdited[index]?.formEditedStatus && validation){
            handleSubmit(postEditSaveHandler)();
        }
        setValidation(true);
    },[saveStatus]) // eslint-disable-line react-hooks/exhaustive-deps
    
    useEffect(() => {
        if(underWritingGroups){
            if (underWritingGroups.percentageOfSelected) {
                setValue('orderPercentage', underWritingGroups.percentageOfSelected);
                saveHandler();
              } else {
                setValue('order', '100');
              }
        }
    }, [underWritingGroups]); // eslint-disable-line react-hooks/exhaustive-deps

    useEffect(()=>{
        if(isBordereauFlag){
            setValue('certificateNumber', fields[9].value);
            setValue('certificateInceptionDate', fields[10].value);
            setValue('certificateExpiryDate', fields[11].value);
        }
    },[uwResetKey]) // eslint-disable-line react-hooks/exhaustive-deps
    
    return(
        <>
            <Typography variant="body2" className={classes.typoTitle}>
                {policyData?.policyNumber}
            </Typography>
            <div className={classes.wrapper}>
                <Layout main padding>
                    <Box m={3}>
                        <Grid container spacing={3} direction="row">
                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui 
                                        {...utils.form.getFieldProps(fields, 'insured', control)} 
                                        error={errors.insured} 
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }}
                                    />
                                </FormGrid>
                            </Grid>

                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey || isEdit}>
                                    <FormAutocompleteMui 
                                        {...utils.form.getFieldProps(fields, 'client', control)} 
                                        error={errors.client} 
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }} 
                                    />
                                </FormGrid>
                            </Grid>

                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey || isEdit}>
                                    <FormAutocompleteMui 
                                        {...utils.form.getFieldProps(fields, 'countryCode', control)} 
                                        error={errors.countryCode} 
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }}
                                    />
                                </FormGrid>
                            </Grid>
                        </Grid>

                        {
                            isBordereauFlag && (
                                <Grid container spacing={3} direction="row">
                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateNumber')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormText {...utils.form.getFieldProps(fields, 'certificateNumber', control)} error={errors.certificateNumber} />
                                        </FormGrid>
                                    </Grid>
                                    
                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateInceptionDate')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormDate
                                                {...utils.form.getFieldProps(fields, 'certificateInceptionDate', control)}
                                                error={errors.certificateInceptionDate}
                                            />
                                        </FormGrid>
                                    </Grid>

                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateExpiryDate')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormDate
                                                {...utils.form.getFieldProps(fields, 'certificateExpiryDate', control)}
                                                error={errors.certificateExpiryDate}
                                            />
                                        </FormGrid>
                                    </Grid>
                                </Grid>
                            )
                        }

                        <Grid container spacing={3} direction="row">
                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'originalCurrencyCode', control)}
                                        error={errors.settlementCurrencyCode}
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }}
                                    />
                                </FormGrid>
                            </Grid>
                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'settlementCurrencyCode', control)}
                                        error={errors.settlementCurrencyCode}
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }}
                                    />
                                </FormGrid>
                            </Grid>
                            <Grid item xs={4}>
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'interest', control)}
                                        error={errors.interest}
                                        callback={initialLoad && saveHandler}
                                        handleUpdate={(id, value) => {
                                            setValue(id, value);
                                        }}
                                    />
                                </FormGrid>
                            </Grid>
                        </Grid>

                    </Box>
                    <Box>
                    <Accordion expanded={accExpandStatus} onChange={()=>setAccExpandStatus(!accExpandStatus)} className={classes.accordionLine} title={<Typography className={classes.title} variant="body2"> {utils.string.t('claims.underWritingGroups.title')}</Typography>}>
                        <Grid container direction="row">
                            <Grid item xs={12}>
                             <ClaimsUnderwritingGroups fields={fields} claimForm={claimForm} uwResetKey={uwResetKey} hasClaimRef={hasClaimRef} selectedPolicyRender={selectedPolicyRender} />
                            </Grid>
                            <Grid item xs={12}>
                            <ClaimsMovementType
                                fields={fields}
                                claimForm={claimForm}
                                underWritingGroups={underWritingGroups}
                                enforceValueSet={true}
                            />
                            </Grid>
                        </Grid>
                    </Accordion>
                    </Box>
                </Layout>
            </div>
            
        </>
    );
}
