import React, { useEffect, useState, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useHistory, useParams } from 'react-router';

// app
import { ClaimDashboardView } from './ClaimDashboard.view';
import { selectClaimsTabRowSelected, showModal, hideModal, returnToTeamQueueClaim, selectIsUserClaim, selectIsClosedClaim, selectIsTeamClaim } from 'stores';
import * as utils from 'utils';

import config from 'config';

export default function ClaimDashboard() {
  const history = useHistory();
  const dispatch = useDispatch();
  const isReassignFormDirtyRef = useRef(false);
  const user = useSelector((state) => state.user);
  const params = useParams();
  const [selectedTab, setSelectedTab] = useState(params?.tab || 'claimRefDetail');
  const [checkRedirectLocation, setCheckRedirectLocation] = useState(false);
  const [claimRefFromLossObj, setClaimRefFromLossObj] = useState({});
  const claimsRowSelected = useSelector(selectClaimsTabRowSelected);
  const claimSelected = claimsRowSelected?.[0];

  const userHasAllClaimsPermission = utils.app.access.feature('claimsProcessing.allClaims', ['read', 'create', 'update'], user);
  //const isReassignEnabled = constants.REASSIGN_ENABLED_TASK_STATUSES.includes(claimSelected?.processState);
  const isUsersClaim = useSelector(selectIsUserClaim);
  const isClosedClaim = useSelector(selectIsClosedClaim);
  const isTeamClaim = useSelector(selectIsTeamClaim);

  const setCheckPage = (val, associateClaimData, lossRef) => {
    setCheckRedirectLocation(val);
    setClaimRefFromLossObj({
      claimID: associateClaimData?.claimID,
      claimRef: associateClaimData?.claimReference,
      lossRef: lossRef,
      sourceId: associateClaimData?.sourceID,
      policyId: associateClaimData?.xbPolicyID,
    });
    if (associateClaimData?.claimReference) {
      history.push(`${config.routes.claimsProcessing.claim}/${associateClaimData?.claimReference}`);
    }
  };

  useEffect(() => {
    if (claimsRowSelected?.length !== 1 || !claimSelected?.claimReference) {
      history.replace(config.routes.claimsFNOL.root);
    }
  }, [history, claimsRowSelected]); // eslint-disable-line react-hooks/exhaustive-deps

  const handleSelectTab = (tabName) => {
    setSelectedTab(tabName);
  };

  const handleCreateRFIModal = (claim) => {
    dispatch(
      showModal({
        component: 'CREATE_RFI',
        props: {
          title: utils.string.t('claims.processing.taskFunction.createRFI'),
          hideCompOnBlur: false,
          fullWidth: true,
          maxWidth: 'md',
          disableAutoFocus: true,
          componentProps: {
            claim,
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };

  const setIsReassignFormDirty = (isDirty) => {
    isReassignFormDirtyRef.current = isDirty;
  };

  const bulkAssignConfirm = () => {
    if (isReassignFormDirtyRef.current) {
      dispatch(
        showModal({
          component: 'CONFIRM',
          props: {
            title: utils.string.t('navigation.form.subtitle'),
            hint: utils.string.t('navigation.form.title'),
            fullWidth: true,
            maxWidth: 'xs',
            componentProps: {
              cancelLabel: utils.string.t('app.no'),
              confirmLabel: utils.string.t('app.yes'),
              submitHandler: () => {
                dispatch(hideModal('SINGLE_ASSIGN_CLAIM'));
              },
            },
          },
        })
      );
    } else {
      dispatch(hideModal('SINGLE_ASSIGN_CLAIM'));
    }
  };

  const handleReassignClaim = (claim) => {
    dispatch(
      showModal({
        component: 'SINGLE_ASSIGN_CLAIM',
        props: {
          title: 'claims.processing.bulkAssign.title',
          fullWidth: true,
          maxWidth: 'sm',
          hideCompOnBlur: false,
          componentProps: {
            claimsProcessingSelected: [claim],
            setIsDirty: setIsReassignFormDirty,
            clickXHandler: () => {
              bulkAssignConfirm();
            },
            cancelHandler: () => {
              bulkAssignConfirm();
            },
          },
        },
      })
    );
  };

  const handleReturnClaimToQueue = (claim) => {
    dispatch(
      showModal({
        component: 'CONFIRM',
        props: {
          title: utils.string.t('status.alert'),
          fullWidth: true,
          maxWidth: 'xs',
          componentProps: {
            cancelLabel: utils.string.t('app.no'),
            confirmLabel: utils.string.t('app.yes'),
            confirmMessage: (
              <span
                dangerouslySetInnerHTML={{
                  __html: `${utils.string.t('claims.complexityRulesManagementDetails.alertModal.note')}`,
                }}
              />
            ),
            submitHandler: () => {
              dispatch(returnToTeamQueueClaim(claim));
            },
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };

  const handleCreateAdhocTask = (claim) => {
    dispatch(
      showModal({
        component: 'CREATE_AD_HOC_TASK',
        props: {
          title: utils.string.t('claims.processing.taskFunction.createAdhocTask'),
          hideCompOnBlur: false,
          fullWidth: true,
          maxWidth: 'md',
          disableAutoFocus: true,
          componentProps: {
            claim,
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };
  const handleReOpenClaim = (claim) => {
    dispatch(
      showModal({
        component: 'CONFIRM',
        props: {
          title: utils.string.t('claims.claimRef.popOverItems.reOpen'),
          hint: utils.string.t('claims.claimRef.popOverItems.reOpenClaimMessage'),
          fullWidth: true,
          maxWidth: 'xs',
          componentProps: {
            cancelLabel: utils.string.t('app.no'),
            confirmLabel: utils.string.t('app.yes'),
            submitHandler: () => {
              handleSetClaimTaskSelection(claim);
            },
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };
  const handleSetClaimTaskSelection = (claim) => {
    dispatch(
      showModal({
        component: 'SET_CLAIM_TASK_SELECTION',
        props: {
          title: utils.string.t('claims.modals.claimFunctions.setClaimTaskSelection'),
          fullWidth: true,
          maxWidth: 'sm',
          disableAutoFocus: true,
          componentProps: {
            claim,
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };

  const popoverItems = [
    {
      id: 'reAssignClaim',
      label: utils.string.t('app.reAssing'),
      disabled: isClosedClaim || !(userHasAllClaimsPermission && isTeamClaim),
      callback: () => handleReassignClaim(claimSelected),
    },
    {
      id: 'createAdhocTask',
      label: utils.string.t('claims.processing.summary.buttons.createTask'),
      disabled: isClosedClaim || !isUsersClaim,
      callback: () => handleCreateAdhocTask(claimSelected),
    },
    {
      id: 'createRFI',
      label: utils.string.t('claims.processing.summary.buttons.createNewRFI'),
      disabled: isClosedClaim,
      callback: () => handleCreateRFIModal(claimSelected),
    },
    {
      id: 'setClaimPriority',
      label: utils.string.t('claims.claimRef.popOverItems.setClaimPriority'),
      disabled: isClosedClaim || !isUsersClaim,
      callback: () => handleSetClaimPriority(claimSelected),
    },
    {
      id: 'claimToTeamQueue',
      label: utils.string.t('claims.processing.summary.buttons.releaseQueue'),
      disabled: isClosedClaim || !isUsersClaim,
      callback: () => handleReturnClaimToQueue(claimSelected),
    },
    {
      id: 'reOpenClaim',
      label: utils.string.t('claims.claimRef.popOverItems.reopenClaim'),
      disabled: !isClosedClaim,
      callback: () => handleReOpenClaim(claimsRowSelected?.[0]),
    },
  ];

  const breadcrumbs = [
    {
      name: 'claimsFnol',
      label: utils.string.t('loss.title'),
      link: config.routes.claimsFNOL.root,
    },
    {
      name: 'lossRef',
      label: `${utils.string.t('loss.text', {
        lossRef: claimSelected?.lossRef,
      })}`,
      link: `${config.routes.claimsFNOL.loss}/${!checkRedirectLocation ? claimSelected?.claimRef : claimRefFromLossObj?.claimRef}`,
      active: true,
    },
    {
      name: 'claimRef',
      label: `${utils.string.t('claims.claimRef.text', {
        claimRef: claimSelected?.claimRef,
      })}`,
      link: `${config.routes.claimsFNOL.claim}/${claimSelected?.claimRef}`,
      active: true,
    }, 
  ];

  const tabs = [
    { value: 'claimRefDetail', label: utils.string.t('claims.claimRef.detail') },
    { value: 'claimRefActions', label: utils.string.t('claims.claimRef.actions') },
    { value: 'claimRefDocs', label: utils.string.t('claims.claimRef.docs') },
    { value: 'claimRefNotes', label: utils.string.t('claims.claimRef.notes') },
    { value: 'claimRefAuditTrail', label: utils.string.t('claims.claimRef.auditTrail') },
  ];

  const handleSetClaimPriority = (claim) => {
    dispatch(
      showModal({
        component: 'SET_CLAIM_PRIORITY',
        props: {
          title: utils.string.t('claims.modals.claimFunctions.setClaimPriority'),
          fullWidth: true,
          maxWidth: 'sm',
          disableAutoFocus: true,
          componentProps: {
            claim,
            cancelHandler: () => {
              dispatch(hideModal());
            },
          },
        },
      })
    );
  };

  // abort
  if (!utils.generic.isValidObject(claimSelected, 'claimReference')) return null;

  return (
    <ClaimDashboardView
      tabs={tabs}
      selectedTab={selectedTab}
      handleSelectTab={handleSelectTab}
      popoverItems={popoverItems}
      claimObj={claimSelected}
      claimRefFromLossObj={claimRefFromLossObj}
      breadcrumbs={breadcrumbs}
      setCheckPage={setCheckPage}
      checkRedirectLocation={checkRedirectLocation}
    />
  );
}
