const styles = (theme) => ({
  root: {
    width: '100%',
    boxShadow: 'none',
  },
  sectionheader: {
    fontWeight: theme.typography.fontWeightMedium,
  },
  fullWidth: {
    display: 'flex',
    justifyContent: 'space-between',
    '& > div:last-child': {
      padding: theme.spacing(0),
    },
  },
  catCodeSelect: {
    '& .MuiBox-root': {
      overflow: 'hidden',
    },
  },
  descriptionBox: {
    '& > div:first-child': {
      paddingRight: theme.spacing(0),
    },
  },
  docsPromptGrid: {
    marginLeft: theme.spacing(4),
  },
  uploadYesBtn: {
    marginLeft: theme.spacing(1),
  },
  submitDocsBtn: {
    margin: '0 auto',
  },
  replyBtnContainer: {
    paddingLeft: '2rem',
    paddingRight: '1rem',
  },
  closeBtn: {
    marginLeft: '12px',
  },
});

export default styles;
