import React, { useEffect, Fragment, useState } from 'react';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';

// app
import useDmsUpload from './useDmsUpload';
import styles from './DmsUpload.styles';
import {
  Button,
  FormContainer,
  FormActions,
  FormText,
  Accordion,
  PopoverMenu,
  FormFields,
  TableHead,
  TableCell,
  FormGrid,
  Info,
  Warning,
  Loader,
  FormDate,
  FormSelect,
  FormAutocompleteMui,
  FormLabel,
} from 'components';
import * as utils from 'utils';
import { resetFilesUploaded, getViewTableDocuments, selectCaseDetails } from 'stores';
import * as constants from 'consts';
import config from 'config';

// mui
import { Table, TableBody, TableRow, Box, Typography, makeStyles, Collapse } from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ErrorOutlineIcon from '@material-ui/icons/ErrorOutline';

DmsUploadView.propTypes = {
  context: PropTypes.string.isRequired,
  referenceId: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
  sourceId: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
  documentTypeKey: PropTypes.oneOf([constants.DMS_DOCUMENT_TYPE_SECTION_KEYS]),
  searchParamsAfterUpload: PropTypes.shape({
    referenceId: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
    sectionType: PropTypes.string.isRequired,
  }),
  postDmsDocumentsSuccess: PropTypes.func,
};

DmsUploadView.defaultProps = {
  documentTypeKey: constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.type.policy,
  postDmsDocumentsSuccess: () => {},
};

export default function DmsUploadView({
  context,
  referenceId,
  sourceId,
  documentTypeKey,
  searchParamsAfterUpload,
  postDmsDocumentsSuccess,
}) {
  const {
    getFileKey,
    fieldsArray,
    control,
    handleRemoveFiles,
    cols,
    commonInfo,
    lossInfo,
    claimInfo,
    policyInfo,
    handleSubmit,
    handleSubmitUpload,
    isPending,
    isFulfilled,
    isRejected,
    getIcons,
    dmsContext,
    handleCancel,
    documentTypes,
    handleSelectedItem,
    documentDto,
    register,
    getValues,
    formState,
    duplicateIndex,
    handleSelectDocClassification,
    docClassificationTypes,
    indexDocTypeUnfilled,
    setValue,
    currencies,
    handleInputChange,
    resetPostStatus,
    handleSingleFileSubmission,
    isActionDispatched,
    postSubmissionRowIndex,
    handleResetFilter,
    refDocumentDto,
    isDmsFromPi,
  } = useDmsUpload(documentTypeKey, sourceId, referenceId, postDmsDocumentsSuccess);
  const classes = makeStyles(styles, { name: 'DmsUpload' })();
  const dispatch = useDispatch();

  const isDmsFromPiInstruction = utils.dmsFormatter.isDmsFromPiInstruction(documentTypeKey);

  const ppCaseDetails = useSelector(selectCaseDetails);
  const [folderName, setFolderName] = useState(null);
  const handleGetList = async (formValues) => {
    await handleSubmitUpload(formValues);
  };

  // Premium Processing Case Details
  const policyRef = ppCaseDetails?.policyRef;
  const instructionId = ppCaseDetails?.instructionId;

  useEffect(() => {
    return () => {
      dispatch(resetFilesUploaded());
      dispatch(resetPostStatus());
      if (!isDmsFromPiInstruction) {
        dispatch(
          getViewTableDocuments({
            ...(searchParamsAfterUpload
              ? searchParamsAfterUpload
              : {
                  referenceId,
                  sectionType: context,
                  documentTypeKey,
                  ...(policyRef && instructionId && { policyRef, instructionId }),
                }),
          })
        );
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const documentTypesItems =
    folderName !== null ? documentTypes?.filter((selectedItem) => selectedItem?.folderName === folderName) : documentTypes;

  const folderTypesItems = [...new Set(documentTypes?.filter((type) => type?.folderName !== null).map((type) => type?.folderName))]
    .sort()
    .map((name) => ({
      id: name,
      label: name,
    }));

  const filterDocumentType = (event, value) => {
    if (value === null) {
      setFolderName(null);
      handleResetFilter();
    } else {
      handleResetFilter();
      setFolderName(value?.label);
    }
  };

  const hasSigleDocumentType = documentTypesItems?.length === 1;

  return (
    <FormContainer type="dialog" onSubmit={handleSubmit(handleGetList)}>
      <FormFields type="dialog">
        {!isDmsFromPi && (
          <Box mx={1}>
            <FormLabel className={classes.selectText} label={utils.string.t('dms.upload.modalItems.selectFolder')} />
            <Box className={classes.formAuto}>
              <FormAutocompleteMui
                options={folderTypesItems}
                defaultValue={null}
                optionKey="id"
                optionLabel="label"
                callback={(event, value) => filterDocumentType(event, value)}
              />
            </Box>
          </Box>
        )}
        <Box mx={1}>
          <Typography className={classes.subTitle}>{`${fieldsArray?.length} ${utils.string.t(
            'dms.upload.modalItems.uploads'
          )}`}</Typography>
        </Box>
        <Table>
          <TableHead columns={cols} />
          <TableBody>
            {fieldsArray?.map(({ file, name, type }, index) => {
              return (
                <Fragment key={index}>
                  <TableRow key={getFileKey(file)} className={classes.root}>
                    <TableCell className={classes.cellWidth}>
                      <FormText
                        control={control}
                        type="textarea"
                        name={`files${index}name`}
                        defaultValue={name}
                        errors={formState.errors}
                        muiComponentProps={{
                          onChange: handleInputChange(`files${index}name`, index),
                          size: 'small',
                          multiline: true,
                          rows: 1,
                          rowsMax: 6,
                        }}
                        compact={true}
                      />

                      {(`files${duplicateIndex}name`.includes(index) || formState.errors[`files${index}name`]) && (
                        <Warning
                          text={
                            `files${duplicateIndex}name`.includes(index)
                              ? utils.string.t('dms.upload.warning.duplicateDocumentWarning')
                              : formState.errors[`files${index}name`].message
                          }
                          type="error"
                          size="small"
                          align="left"
                          icon={ErrorOutlineIcon}
                        />
                      )}
                    </TableCell>
                    <TableCell className={classes.cellWidth}>
                      <PopoverMenu
                        {...register(`files${index}`)}
                        text={
                          hasSigleDocumentType
                            ? documentTypesItems[0]?.documentTypeDescription
                            : getValues(`files${index}`)?.documentTypeDescription ||
                              utils.string.t('dms.upload.modalItems.selectDocumentType')
                        }
                        size="small"
                        id={`files${index}`}
                        icon={ArrowDropDownIcon}
                        iconPosition="right"
                        disabled={hasSigleDocumentType ? true : false}
                        anchorOrigin={{
                          vertical: 'bottom',
                          horizontal: 'left',
                        }}
                        transformOrigin={{
                          vertical: 'top',
                          horizontal: 'center',
                        }}
                        items={documentTypesItems.map((selectedItem, i) => ({
                          id: i,
                          label: selectedItem.documentTypeDescription,
                          callback: () => handleSelectedItem(selectedItem, index),
                        }))}
                      />
                      {indexDocTypeUnfilled.includes(index) && !documentDto[index]?.documentUploaded && isActionDispatched && (
                        <Warning
                          text={utils.string.t('dms.upload.warning.emptyDocumentType')}
                          type="error"
                          size="small"
                          align="left"
                          icon={ErrorOutlineIcon}
                        />
                      )}
                    </TableCell>
                    <TableCell className={classes.cellWidth}>
                      <PopoverMenu
                        {...register(`filesClassification${index}`)}
                        text={
                          getValues(`filesClassification${index}`)?.value || utils.string.t('dms.upload.modalItems.classificationType.high')
                        }
                        size="small"
                        id={`filesClassification${index}`}
                        icon={ArrowDropDownIcon}
                        iconPosition="right"
                        disabled={false}
                        anchorOrigin={{
                          vertical: 'bottom',
                          horizontal: 'left',
                        }}
                        transformOrigin={{
                          vertical: 'top',
                          horizontal: 'center',
                        }}
                        items={docClassificationTypes.map((selectedItem, i) => ({
                          id: i,
                          label: selectedItem.value,
                          callback: () => handleSelectDocClassification(selectedItem, index),
                        }))}
                      />
                    </TableCell>
                    <TableCell className={classes.cellWidth}>
                      <PopoverMenu
                        text={utils.string.t('dms.upload.modalItems.addTag')}
                        size="small"
                        id="document-tags"
                        icon={ArrowDropDownIcon}
                        iconPosition="right"
                        disabled={false}
                        anchorOrigin={{
                          vertical: 'bottom',
                          horizontal: 'left',
                        }}
                        transformOrigin={{
                          vertical: 'top',
                          horizontal: 'center',
                        }}
                        items={[]}
                      />
                    </TableCell>
                    <TableCell className={classes.cellWidth}>
                      {!isPending && (
                        <Button
                          icon={getIcons(isFulfilled, isRejected, documentDto[index]?.documentUploaded)}
                          variant="text"
                          danger={isRejected || (isFulfilled && !documentDto[index]?.documentUploaded)}
                          nestedClasses={{
                            btn: isFulfilled && documentDto[index]?.documentUploaded ? classes.iconSuccess : '',
                          }}
                          size="small"
                          onClick={handleRemoveFiles(name)}
                        />
                      )}
                      {isFulfilled && !documentDto[index]?.documentUploaded && (
                        <Button
                          text={utils.string.t('dms.upload.modalItems.retryUpload')}
                          variant="text"
                          size="small"
                          color="secondary"
                          nestedClasses={{ btn: classes.btnRetry }}
                          onClick={() => handleGetList(handleSingleFileSubmission(index))}
                        />
                      )}
                      <Loader
                        visible={
                          (isPending && !Boolean(documentDto.length)) || (isPending && `files${index}name`.includes(postSubmissionRowIndex))
                        }
                        inline={
                          (isPending && !documentDto[index]?.documentUploaded) ||
                          (isPending && `files${index}name`.includes(postSubmissionRowIndex))
                        }
                      />
                    </TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                      <Collapse
                        in={getValues(`files${index}`)?.documentTypeDescription === constants.DMS_DOCUMENT_TYPE_PAYMENT}
                        timeout="auto"
                      >
                        <Box my={2}>
                          <FormGrid container spacing={2}>
                            <FormGrid item xs={12} sm={2} md={2}>
                              <FormDate
                                control={control}
                                {...register(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment date`)}
                                label={utils.string.t('dms.upload.paymentDetails.paymentDate')}
                                name={`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment date`}
                                value={getValues(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment date`)}
                                type="datepicker"
                                defaultValue={null}
                                handleUpdate={(name, value) =>
                                  setValue(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment date`, value)
                                }
                                outputFormat="iso"
                                muiComponentProps={{
                                  classes: {
                                    root: classes.datepicker,
                                  },
                                }}
                              />
                            </FormGrid>
                            <FormGrid item xs={12} sm={2} md={2}>
                              <FormText
                                control={control}
                                {...register(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment Reference`)}
                                label={utils.string.t('dms.upload.paymentDetails.enterReference')}
                                value={getValues(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment Reference`)}
                                name={`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Payment Reference`}
                                defaultValue=""
                              />
                            </FormGrid>
                            <FormGrid item xs={12} sm={2} md={2}>
                              <FormText
                                control={control}
                                {...register(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Loss Payee`)}
                                value={getValues(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Loss Payee`)}
                                name={`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Loss Payee`}
                                label={utils.string.t('dms.upload.paymentDetails.enterName')}
                                defaultValue=""
                              />
                            </FormGrid>
                            <FormGrid item xs={12} sm={2} md={2}>
                              <FormText
                                type="number"
                                control={control}
                                value={getValues(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Amount`)}
                                name={`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Amount`}
                                label={utils.string.t('dms.upload.paymentDetails.enterAmount')}
                                defaultValue=""
                                {...register(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Amount`)}
                              />
                            </FormGrid>
                            <FormGrid item xs={12} sm={2} md={2}>
                              <FormSelect
                                control={control}
                                {...register(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Currency`)}
                                label={utils.string.t('dms.upload.paymentDetails.selectCurrency')}
                                name={`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Currency`}
                                options={currencies}
                                optionKey="id"
                                optionLabel="name"
                                defaultValue=""
                                handleUpdate={(name, value) => setValue(`${constants.DMS_DOCUMENT_TYPE_PAYMENT}[${index}].Currency`, value)}
                              />
                            </FormGrid>
                          </FormGrid>
                        </Box>
                      </Collapse>
                    </TableCell>
                  </TableRow>
                </Fragment>
              );
            })}
          </TableBody>
        </Table>
        <Accordion title={<Typography>{utils.string.t('dms.upload.modalItems.viewData')}</Typography>}>
          <Box width="100%">
            <Box className={classes.boxView}>
              <Typography className={classes.subTitle}>{utils.string.t('dms.upload.commonInfoSection.commonInfo')}</Typography>
              <FormGrid container spacing={3}>
                {commonInfo?.map(({ name, value, id }) => (
                  <FormGrid key={id} item xs={12} sm={4} md={3}>
                    <Info title={name} description={value} nestedClasses={{ root: classes.info }} />
                  </FormGrid>
                ))}
                {isFulfilled &&
                  formState.isSubmitSuccessful &&
                  refDocumentDto?.map((doc, i) => (
                    <Fragment key={i}>
                      <FormGrid item xs={12} sm={4} md={3}>
                        <Info
                          title={utils.string.t('dms.upload.commonUploadInfoSection.documentCreatedOn')}
                          description={`${utils.string.t('format.date', {
                            value: { date: doc.createdDate, format: config.ui.format.date.text },
                          })}`}
                          nestedClasses={{ root: classes.info }}
                        />
                      </FormGrid>
                      <FormGrid item xs={12} sm={4} md={3}>
                        <Info
                          title={utils.string.t('dms.upload.commonUploadInfoSection.documentVersion')}
                          description={doc.documentVersion}
                          nestedClasses={{ root: classes.info }}
                        />
                      </FormGrid>
                      <FormGrid item xs={12} sm={4} md={3}>
                        <Info
                          title={utils.string.t('dms.upload.commonUploadInfoSection.documentUploadedBy')}
                          description={doc.createdByName}
                          nestedClasses={{ root: classes.info }}
                        />
                      </FormGrid>
                      <FormGrid item xs={12} sm={4} md={3}>
                        <Info
                          title={utils.string.t('dms.upload.commonUploadInfoSection.documentModifiedOn')}
                          description={`${utils.string.t('format.date', {
                            value: { date: doc.fileLastModifiedDate, format: config.ui.format.date.text },
                          })}`}
                          nestedClasses={{ root: classes.info }}
                        />
                      </FormGrid>
                    </Fragment>
                  ))}
              </FormGrid>
            </Box>
            {dmsContext === constants.DMS_CONTEXT_CLAIM && (
              <Box className={classes.boxView} mt={2}>
                <Typography className={classes.subTitle}>{utils.string.t('dms.upload.claimInfoSection.claimDetails')}</Typography>
                <FormGrid container spacing={3}>
                  {claimInfo?.map(({ name, value, id }) => (
                    <FormGrid key={id} item xs={12} sm={4} md={3}>
                      <Info title={name} description={value} nestedClasses={{ root: classes.info }} />
                    </FormGrid>
                  ))}
                </FormGrid>
              </Box>
            )}
            {(dmsContext === constants.DMS_CONTEXT_CLAIM || dmsContext === constants.DMS_CONTEXT_LOSS) && (
              <Box className={classes.boxView} mt={2}>
                <Typography className={classes.subTitle}>{utils.string.t('dms.upload.lossInfoSection.lossDetails')}</Typography>
                <FormGrid container spacing={3}>
                  {lossInfo?.map(({ name, value, id }) => (
                    <FormGrid key={id} item xs={12} sm={4} md={3}>
                      <Info title={name} description={value} nestedClasses={{ root: classes.info }} />
                    </FormGrid>
                  ))}
                </FormGrid>
              </Box>
            )}
            {(dmsContext === constants.DMS_CONTEXT_PROCESSING_INSTRUCTION ||
              dmsContext === constants.DMS_CONTEXT_POLICY ||
              dmsContext === constants.DMS_CONTEXT_CASE) && (
              <Box className={classes.boxView} mt={2}>
                <Typography className={classes.subTitle}>{utils.string.t('dms.upload.policyInfoSection.policyDetails')}</Typography>
                <FormGrid container spacing={3}>
                  {policyInfo?.map(({ name, value, id }) => (
                    <FormGrid key={id} item xs={12} sm={4} md={3}>
                      <Info title={name} description={value} nestedClasses={{ root: classes.info }} />
                    </FormGrid>
                  ))}
                </FormGrid>
              </Box>
            )}
          </Box>
        </Accordion>
      </FormFields>
      <FormActions type="dialog">
        <Button text={utils.string.t('dms.upload.modalItems.cancel')} variant="text" onClick={handleCancel()} />
        <Button
          text={utils.string.t('dms.upload.modalItems.saveUpload')}
          disabled={(isFulfilled && formState.isSubmitSuccessful) || ((isPending || isRejected) && formState.isSubmitSuccessful)}
          type="submit"
          color="primary"
        />
      </FormActions>
    </FormContainer>
  );
}
