import React from 'react';
import { useForm } from 'react-hook-form';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';

import { select, withKnobs, text, object, boolean } from '@storybook/addon-knobs';
import { Box } from '@material-ui/core';

import { FormPopoverMenuRHF } from 'components';

export default {
  title: 'FormPopoverMenuRHF',
  component: FormPopoverMenuRHF,
  decorators: [withKnobs],
};

export const Default = () => {
  const { control } = useForm({
    defaultValues: [],
  });

  const popoverButton = {
    textTransform: 'inherit',
  };

  const fields = [
    {
      name: 'Menu',
      type: 'SELECT',
      indicative: false,
      group: 'GENERAL',
      label: 'New or Renewal',
      options: [
        {
          label: 'Send Email',
          id: 'EMAIL',
        },
        {
          label: 'Drafts',
          id: 'DRAFTS',
        },
        {
          label: 'Inbox',
          id: 'INBOX',
        },
      ],
      validation: {
        required: true,
        message: 'The insurance type field value cannot null (New / Renewal)',
      },
    },
  ];

  return (
    <Box>
      {fields.map((field, index) => {
        return (
          <FormPopoverMenuRHF
            control={control}
            name={field.name}
            placeholder={text('placeholder', 'select')}
            text={text('Menu', 'Open Menu')}
            size={select('Size', ['xsmall', 'small', 'medium', 'large'], 'small')}
            icon={ArrowDropDownIcon}
            iconPosition={select('IconPosition', ['left', 'right'], 'right')}
            disabled={boolean('Disabled', false)}
            anchorOrigin={object('AnchorOrigin', {
              vertical: 'bottom',
              horizontal: 'left',
            })}
            transformOrigin={object('TransformOrigin', {
              vertical: 'top',
              horizontal: 'center',
            })}
            nestedClasses={{ btn: popoverButton }}
            hidden={boolean('Hidden', false)}
            offset={boolean('offset', false)}
            data={object('Data', {})}
            items={object(
              'Items',
              field.options.map(({ id, label }) => ({
                id,
                label,
                callback: () => alert(`${label} selected`),
              }))
            )}
          />
        );
      })}
    </Box>
  );
};
