import React from 'react';
import PropTypes from 'prop-types';

//app
import { Search, Tooltip, Layout } from 'components';
import * as utils from 'utils';
import styles from './TreeView.styles';
import { DmsDocDetailsTooltip } from 'modules';

//mui
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { TreeView as TreeViewMui, TreeItem } from '@material-ui/lab';
import { Box, Typography, Divider, makeStyles, Grid } from '@material-ui/core';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import DescriptionIcon from '@material-ui/icons/Description';

TreeView.propTypes = {
  rootRef: PropTypes.string.isRequired,
  treeData: PropTypes.shape({
    id: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    labelInfo: PropTypes.string,
    children: PropTypes.array.isRequired,
  }),
  handleAccordionClick: PropTypes.func.isRequired,
  widgetTitle: PropTypes.string,
  columns: PropTypes.arrayOf({ header: PropTypes.string, flex: PropTypes.string }),
  defaultExpanded: PropTypes.arrayOf(PropTypes.string.isRequired),
  filteredData: PropTypes.arrayOf(PropTypes.object),
  handlers: PropTypes.shape({
    submitSearch: PropTypes.func.isRequired,
    resetSearch: PropTypes.func.isRequired,
    rowClick: PropTypes.func.isRequired,
  }),
};

export default function TreeView({
  treeData,
  rootRef,
  handleAccordionClick,
  widgetTitle,
  columns,
  defaultExpanded,
  filteredData,
  handlers,
}) {
  const classes = makeStyles(styles, { name: 'TreeView' })();

  const renderTree = (nodes) => {
    const hasChildren = !utils.generic.isInvalidOrEmptyArray(nodes?.children);
    return (
      <TreeItem
        key={nodes?.id}
        nodeId={`${nodes?.id}`}
        label={
          <Box className={classes.rootTree}>
            <Grid container>
              <Grid item xs={7}>
                <Typography variant="body2" className={classes.labelCol}>
                  {nodes?.labelInfo ? (
                    <Tooltip title={<DmsDocDetailsTooltip data={nodes} />} placement={'bottom'} arrow>
                      {<span className={nodes?.isUploadedOnGxb === 1 ? classes.gxbLabel : classes.fileLabel}>{nodes?.label}</span>}
                    </Tooltip>
                  ) : (
                    nodes?.label
                  )}
                </Typography>
              </Grid>
              <Grid item xs={5}>
                <Typography className={classes.lebelInfoCol} variant="caption" color="inherit">
                  <Tooltip title={<DmsDocDetailsTooltip data={nodes} />} placement={'bottom'} arrow>
                    {nodes?.labelInfo}
                  </Tooltip>
                </Typography>
              </Grid>
            </Grid>
          </Box>
        }
        onClick={(e) => !hasChildren ? handlers.rowClick(e, nodes) : null}
      >
        {!hasChildren ? null : nodes?.children?.map((node) => renderTree(node))}
      </TreeItem>
    )
  };

  const SearchComponent = (
    <Search
      key={'dmsSearch'}
      text={''}
      placeholder={utils.string.t('dms.view.searchDocuments')}
      submitButtonProps={{ size: 'small' }}
      minChars={3}
      nestedClasses={{
        inputPropsRoot: classes.inputPropsRoot,
      }}
      handlers={{
        search: (args) => {
          handlers.submitSearch(args);
        },
        reset: () => {
          handlers.resetSearch();
        },
      }}
    />
  );

  return (
    <Layout sidebar padding={false}>
      <Box pt={2} display="flex" flexDirection="column" className={classes.dmsBoxWidth}>
        <Box display="flex" alignItems="center" mb="20px">
          <DescriptionIcon />
          <Typography variant="body1">{widgetTitle}</Typography>
        </Box>
        <Box mr={2}>{SearchComponent}</Box>
        <Box pt={2} pl={1} pr={1}>
          <Divider />
          <Box display="flex">
            {columns.map(({ header, flex, ml }) => {
              return (
                <Box flex={flex} pl={2} ml={ml}>
                  <Typography variant="body2">{header}</Typography>
                </Box>
              );
            })}
          </Box>
          <Divider />
          {filteredData?.length ? (
            filteredData?.map((data) => {
              return (
                <Box display="flex">
                  <Box flex={0.8} pl={2}>
                    <Tooltip title={<DmsDocDetailsTooltip data={data} />} placement={'bottom'} arrow>
                      <Typography variant="body2">{data?.label}</Typography>
                    </Tooltip>
                  </Box>
                  <Box flex={0.5} pl={2}>
                    <Typography variant="body2">{data?.labelInfo}</Typography>
                  </Box>
                </Box>
              );
            })
          ) : (
            <TreeViewMui
              aria-label="rich object"
              defaultCollapseIcon={<ExpandMoreIcon />}
              defaultExpanded={defaultExpanded}
              defaultExpandIcon={<ChevronRightIcon />}
              sx={{ height: 110, flexGrow: 1, maxWidth: 400, overflowY: 'auto' }}
              onNodeSelect={handleAccordionClick}
            >
              {rootRef ? renderTree(treeData) : null}
            </TreeViewMui>
          )}
        </Box>
      </Box>
    </Layout>
  );
}
