import React from 'react';

//app
import styles from './LossActionTable.styles';
import { Empty, Overflow, TableHead } from 'components';
import {LossActionTableRowView } from './LossActionTableRow.view';
import { ReactComponent as IconSearchFile } from '../../assets/svg/line-icon-search-file.svg';
import { useSort } from 'hooks';
import { RowTheme } from './RowTheme';
import * as utils from 'utils';

//mui
import { makeStyles, Table, TableBody, Grid, Box } from '@material-ui/core';

LossActionTableView.prototype = {};

export function LossActionTableView({
  rows,
  itemsData,
  sort: sortObj,
  cols: colsArr,
  columnProps,
  handleSort,
  handleViewClaimClick,
  handleCreateClaim,
  handleEditClaim,
}) {
  const classes = makeStyles(styles, { name: 'SearchTable' })();
  const { cols, sort } = useSort(colsArr, sortObj, handleSort);
  const hasRows = utils.generic.isValidArray(rows, true);


  return (
    <Box data-testid="claims-search-table" className={classes.wrapper}>
      <Overflow>
        <Table data-testid="claims-table">
          <TableHead columns={cols} sorting={sort} nestedClasses={{ tableHead: classes.tableHead }} />
          
            {hasRows &&
              itemsData.map((datalevel1, index) => {

                const level2 = datalevel1.actionChildItemList.map((datalevel2, index) => {
                  return (
                   
                      <RowTheme
                        columnProps={columnProps}
                        data={datalevel2}
                      />
                  
                  );
                });
                return (
                  <>
                    <TableBody data-testid="claims-list" className="level1">
                      <RowTheme
                        columnProps={columnProps}
                        data={datalevel1}
                      />
                    </TableBody>
                    <TableBody data-testid="claims-list" className="level2">
                    {level2}
                    </TableBody>
                   </>
                );

                })}

            {/*hasRows &&
              rows.map((search, index) => {
                return (
                  <LossActionTableRowView
                    data={search}
                    handleCreateClaim={handleCreateClaim}
                    handleViewClaimClick={handleViewClaimClick}
                    columnProps={columnProps}
                    handleEditClaim={handleEditClaim}
                  />
                );
              })*/}
         
        </Table>
      </Overflow>
      {rows ? (
        rows.length ? (
          <Grid container>
            <Grid item xs={12}>
            </Grid>
          </Grid>
        ) : (
          <Grid item xs={12}>
            <Empty title={utils.string.t('claims.noMatchFound')} icon={<IconSearchFile />} padding />
          </Grid>
        )
      ) : (
        <Grid item xs={12}>
          <Empty title={utils.string.t('claims.searchLossAndClaims')} icon={<IconSearchFile />} padding />
        </Grid>
      )}
    </Box>
  );
}
