import get from 'lodash/get';

import { authLogout, addLoader, enqueueNotification, removeLoader } from 'stores';
import * as utils from 'utils';
import * as constants from 'consts';

export const postEditClaimDetailsInformation = (formData) => (dispatch, getState) => {
  // prettier-ignore
  const { user: { auth }, user, config: { vars: { endpoint } }, claims } = getState();

  const defaultError = {
    file: 'stores/claims.actions.postEditClaimDetailsInformation',
  };

  dispatch(postEditClaimDetailsInformationRequest(formData));
  dispatch(addLoader('postEditClaimDetailsInformation'));

  const nowIsoString = utils.date.toISOString(new Date());

  const assignedFullName = get(user, 'fullName');
  const defaultValues = get(claims, 'allClaimDetails');
  const claimInformationValues = get(claims, 'claimDetailInformationSuccess');
  const claimantNames = get(claims, 'claimantNames');
  const referralValues = get(claims, 'referralValues');
  const referralResponse = get(claims, 'referralResponse');
  const underWritingGroups = get(claims, 'underWritingGroups.items');
  const newClaimants = claimantNames.filter((claimant) => typeof claimant.id === 'number').map((claimant) => claimant.name);
  const policyUnderWritingGroupDtoList = underWritingGroups.filter((ug) => ug.selected);
  const selectedbeAdjuster = get(claims, 'beAdjuster.selectedbeAdjuster');
  const beAdjusterID = formData?.adjuster === 'beAdjuster' ? selectedbeAdjuster?.id : claimInformationValues?.beAdjusterID;
  const nonBEAdjusterName = formData?.adjuster === 'nonBeAdjuster' ? formData.nonBeAdjuster : claimInformationValues?.nonBEAdjusterName;
  const policyInterestID = get(claims, 'interest')?.items[0]?.policyInterestID;
  const claimSubmissionId = get(claims, 'claimData').claimId || '';
  const claimStatusId = get(claims, 'claimsInformation')?.claimStatusID || '';
  const claimReference = get(claims, 'claimsInformation')?.claimReference;

  const referralValuesAssignedTo = referralValues
    .find((item) => item.complexityRulesID === Number(formData.referral || claimInformationValues?.referralValueID))
    ?.complexityRulesValue.includes('Assign to Myself');

  const referralResponseAssignedTo = referralResponse
    .find((item) => item.id === formData.rfiResponse)
    ?.description.includes('Assign to Myself');

  const refRequired = 'Referral Required';
  const addDateAndTime = (date, time) => {
    if (!date) return date;
    let actualDate = new Date(
      utils.string.t('format.date', { value: { date: date, format: 'D MMM YYYY' } }) + ' ' + (time ? time + ':00' : '00:00:00')
    );
    return actualDate || date;
  };

  const data = {
    ...defaultValues,
    assignedTo: referralValuesAssignedTo || referralResponseAssignedTo ? assignedFullName : 'Unassigned',
    basisOfOrder: formData?.order || claimInformationValues?.basisOfOrder || '0',
    claimID: claimSubmissionId,
    adjusterName: formData.adjusterName || claimInformationValues?.adjusterName,
    adjusterReference: formData?.adjusterReference || claimInformationValues?.adjusterReference,
    fgunarrative: formData?.fgunarrative || claimInformationValues?.fgunarrative,
    location: formData.location || claimInformationValues?.location,
    lossQualifierID: formData?.lossQualifierID?.id || claimInformationValues?.lossQualifierID || '',
    lossQualifierName: formData?.lossQualifierID?.name || claimInformationValues?.lossQualifierName || '',
    movementType: formData.movementType || claimInformationValues?.movementType,
    orderPercentage: formData.orderPercentage || claimInformationValues?.orderPercentage,
    settlementCurrencyCode: formData?.settlementCurrencyCode?.currencyCd || claimInformationValues?.settlementCurrencyCode,
    claimantName: formData.claimantName?.name || claimInformationValues?.claimantName,
    lossFromDate: utils.date.toISOString(formData?.fromDate) || claimInformationValues?.lossFromDate,
    lossToDate: utils.date.toISOString(formData?.toDate) || claimInformationValues?.lossToDate,
    newClaimants,
    createdBy: user.id,
    createdDate: nowIsoString,
    policySectionID:
      (formData?.ugSections?.id === constants.CLAIM_POLICY_SECTION_DEFAULT ? null : formData?.ugSections?.id) ||
      claimInformationValues?.policySectionID,
    policyUnderWritingGroupDtoList,
    updatedBy: user.id,
    updatedDate: nowIsoString,
    submittedDate: nowIsoString,
    claimStatusID: claimStatusId,
    claimReference: claimReference,
    priorityDescription: formData?.priority?.description || claimInformationValues?.priorityDescription || 'Medium',
    priorityID: formData?.priority?.id || claimInformationValues?.priorityID || 3,
    beAdjusterID,
    nonBEAdjusterName,
    policyInterestID,
    certificateExpiryDate: utils.date.toISOString(formData?.certificateExpiryDate) || claimInformationValues?.certificateExpiryDate,
    certificateInceptionDate:
      utils.date.toISOString(formData?.certificateInceptionDate) || claimInformationValues?.certificateInceptionDate,
    certificateNumber: formData?.certificateNumber || claimInformationValues?.certificateNumber,
    complexityValueID: formData?.complexity?.complexityRulesID || claimInformationValues?.complexityValueID,
    complexityValue: formData?.complexity?.complexityRulesValue || claimInformationValues?.complexityValue,
    referralValueID:
      formData?.complexity?.complexityRulesValue === refRequired
        ? formData?.referral?.complexityRulesID
        : claimInformationValues?.referralValueID,
    referralValue:
      formData?.complexity?.complexityRulesValue === refRequired
        ? formData?.referral?.complexityRulesValue
        : claimInformationValues?.referralValue,
    referralResponseID:
      formData?.complexity?.complexityRulesValue === refRequired ? formData?.rfiResponse?.id : claimInformationValues?.referralResponseID,
    referralResponseDescription:
      formData?.complexity?.complexityRulesValue === refRequired
        ? formData?.rfiResponse?.description
        : claimInformationValues?.referralResponseDescription,
    claimReceivedDate:
      addDateAndTime(formData?.claimReceivedDate, formData?.claimReceivedTime) || addDateAndTime(claimInformationValues?.claimReceivedDate),
    bordereauPeriod: formData?.bordereauPeriod?.id || claimInformationValues?.bordereauPeriod,
    bordereauClaim: formData?.bordereauClaim ? 1 : claimInformationValues?.bordereauClaim,
    insuredID: parseInt(formData?.insured?.id) || claimInformationValues?.insuredID,
    clientID: parseInt(formData?.client?.id) || claimInformationValues?.clientID,
    settlementCountry: formData?.countryCode?.countryCode || claimInformationValues?.settlementCountry,
    originalCurrency: formData?.originalCurrencyCode?.currencyCd || claimInformationValues?.originalCurrency,
  };

  console.log("jenea", data)

  return utils.api
    .put({
      token: auth.accessToken,
      endpoint: endpoint.claimService,
      path: `api/data/claims/${claimSubmissionId}/update`,
      data,
    })
    .then((response) => {
      console.log("response ", response)
      return utils.api.handleResponse(response)
    })   //utils.api.handleResponse(response)  console.log("response ", response)
    .then((json) => utils.api.handleNewData(json))
    .then((data) => {
      dispatch(postEditClaimDetailsInformationSuccess(data.data));
      dispatch(enqueueNotification('notification.claimEditDetails.success', 'success'));
      dispatch(removeLoader('postEditClaimDetailsInformation'));
      return data.data;
    })
    .catch((err) => {
      const errorParams = {
        ...defaultError,
        message: 'API post error',
      };

      utils.api.handleError(err, errorParams);
      utils.api.handleUnauthorized(err, dispatch, authLogout);
      dispatch(postEditClaimDetailsInformationFailure(err));
      dispatch(enqueueNotification('notification.claimEditDetails.fail', 'error'));
      dispatch(removeLoader('postEditClaimDetailsInformation'));
      return err;
    });
};

export const postEditClaimDetailsInformationRequest = (data) => {
  return {
    type: 'CLAIMS_DETAILS_INFORMATION_UPDATE_REQUEST',
    payload: data,
  };
};

export const postEditClaimDetailsInformationSuccess = (data) => {
  return {
    type: 'CLAIMS_DETAILS_INFORMATION_UPDATE_SUCCESS',
    payload: data,
  };
};

export const postEditClaimDetailsInformationFailure = (error) => {
  return {
    type: 'CLAIMS_DETAILS_INFORMATION_UPDATE_FAILURE',
    payload: error,
  };
};
