import React, { useState } from 'react';
import PropTypes from 'prop-types';

//app
import {LossActionLevelThree  } from './LossActionLevelThree'
import { TableRowHeader } from 'modules';
LossActionLevelClaim.prototype = {
    data: PropTypes.array.isRequired,
    columnProps: PropTypes.array.isRequired,
};

export function LossActionLevelClaim({ data, columnProps }){
    const [isOpen, setIsOpen] = useState(false);

    return (
        <TableRowHeader 
            title={`Claim ${data.claimRef}`}
            subtitle="International Energy - Plant, Machinery and Equipment - Terraseis 1040 Limited"
            isOpen={isOpen}
            onClick={() => setIsOpen(!isOpen)}
        >
            {isOpen && data.actionChildItemList.map((itemLevelThree, index) => {
                return(
                    <LossActionLevelThree
                        key={index} 
                        data={itemLevelThree}
                        columnProps={columnProps}
                    />
                )
            })}
        </TableRowHeader>
    );
}
