import React from 'react';
import PropTypes from 'prop-types';

//app
import { Breadcrumb, Layout, Tabs, PopoverMenu, SectionHeader } from 'components';
import { LossDetail, LossAction, ClaimsUploadViewSearchDocs } from 'modules';
import * as utils from 'utils';
import * as constants from 'consts';

//mui
import { Box, Divider } from '@material-ui/core';
import DescriptionIcon from '@material-ui/icons/Description';

LossDashboardView.propTypes = {
  tabs: PropTypes.array.isRequired,
  lossSelected: PropTypes.object.isRequired,
  selectedTab: PropTypes.string.isRequired,
  handleSelectTab: PropTypes.func,
  popoverItems: PropTypes.array.isRequired,
  breadcrumbs: PropTypes.arrayOf(
    PropTypes.shape({
      name: PropTypes.string.isRequired,
      label: PropTypes.string.isRequired,
      link: PropTypes.string.isRequired,
    })
  ).isRequired,
};

export function LossDashboardView({ tabs, selectedTab, lossSelected, handleSelectTab, breadcrumbs }) {
  return (
    <>
      <Breadcrumb links={breadcrumbs} />
      <Divider />
      <Layout testid="LossDashboard">
        <Layout main>
          <SectionHeader
            title={utils.string.t('loss.text', {
              claimRef: 1234,
            })}
            icon={DescriptionIcon}
            testid="claim-ref-header"
          >
            <PopoverMenu
              variant="outlined"
              id="loss-functions"
              size="small"
              color="primary"
              text={utils.string.t('loss.popOverFunction')}
              isButton
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
            />
          </SectionHeader>
          <Tabs tabs={tabs} value={selectedTab} onChange={(tabName) => handleSelectTab(tabName)} />
          {selectedTab === 'lossDetail' && (
            <LossDetail lossObj={lossSelected} />
          )}
          {selectedTab === 'lossAction' && (
            <LossAction />
          )}
          {selectedTab === 'lossDocs' && (
            <Box mt={2}>
              <ClaimsUploadViewSearchDocs
                refData={lossSelected}
                refIdName={constants.DMS_CONTEXT_LOSS_ID}
                dmsContext={constants.DMS_CONTEXT_LOSS}
                documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.type.claims}
              />
            </Box>
          )}
        </Layout>
      </Layout>
    </>
  );
}
