import React from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import PropTypes from 'prop-types';

//app
import * as utils from 'utils';

import { Accordion, FormAutocompleteMui, FormDate, FormLabel, FormGrid, FormText, Layout } from 'components';
import { ClaimsUnderwritingGroups, ClaimsMovementType } from 'modules';
import styles from './LinkClaimPolicy.styles';

import { makeStyles, Grid, Box, Typography } from '@material-ui/core';

export function LinkClaimPolicyView({fields, policyData, uwResetKey, hasClaimRef, underWritingGroups, claimPolicyClients, isBordereauFlag}){
    //console.log(policyData);
    const classes = makeStyles(styles, { name: 'EnterClaimInformation' })();

    const defaultValues = utils.form.getInitialValues(fields);
    const validationSchema = utils.form.getValidationSchema(fields);

    const claimForm = useForm({
        defaultValues,
        ...(validationSchema && { resolver: yupResolver(validationSchema) }),
        //...(validationSchema && { resolver: yupResolver(validationSchema), context: { validation } }),
    });
    const { handleSubmit, setValue, control, errors } = claimForm;
    
    return(
        <>
            <Typography variant="body2" className={classes.typoTitle}>
                {policyData?.policyNumber}
            </Typography>
            <div className={classes.wrapper}>
                <Layout main padding>
                    <Box m={3}>
                        <Grid container spacing={3} direction="row">
                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.insured')}`}
                                    align="right"
                                />
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'insured', control)} error={errors.insured} />
                                </FormGrid>
                            </Grid>

                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.client')}`}
                                    align="right"
                                />
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui {...utils.form.getFieldProps(fields, 'client', control)} error={errors.client} />
                                </FormGrid>
                            </Grid>

                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.country')}`}
                                    align="right"
                                />
                            </Grid>
                        </Grid>

                        {
                            isBordereauFlag && (
                                <Grid container spacing={3} direction="row">
                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateNumber')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormText {...utils.form.getFieldProps(fields, 'certificateNumber', control)} error={errors.certificateNumber} />
                                        </FormGrid>
                                    </Grid>
                                    
                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateInceptionDate')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormDate
                                                {...utils.form.getFieldProps(fields, 'certificateInceptionDate', control)}
                                                error={errors.certificateInceptionDate}
                                            />
                                        </FormGrid>
                                    </Grid>

                                    <Grid item xs={4}>
                                        <FormLabel
                                            nestedClasses={{ root: classes.formLabel }}
                                            label={`${utils.string.t('claims.claimInformation.certificateExpiryDate')}`}
                                            align="right"
                                        />
                                        <FormGrid item>
                                            <FormDate
                                                {...utils.form.getFieldProps(fields, 'certificateExpiryDate', control)}
                                                error={errors.certificateExpiryDate}
                                            />
                                        </FormGrid>
                                    </Grid>
                                </Grid>
                            )
                        }

                        <Grid container spacing={3} direction="row">
                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.settlementCurrency')}`}
                                    align="right"
                                />
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'originalCurrencyCode', control)}
                                        error={errors.settlementCurrencyCode}
                                    />
                                </FormGrid>
                            </Grid>
                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.settlementCurrency')}`}
                                    align="right"
                                />
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'settlementCurrencyCode', control)}
                                        error={errors.settlementCurrencyCode}
                                    />
                                </FormGrid>
                            </Grid>
                            <Grid item xs={4}>
                                <FormLabel
                                    nestedClasses={{ root: classes.formLabel }}
                                    label={`${utils.string.t('claims.claimInformation.interest')}`}
                                    align="right"
                                />
                                <FormGrid item key={uwResetKey}>
                                    <FormAutocompleteMui
                                        {...utils.form.getFieldProps(fields, 'interest', control)}
                                        error={errors.interest}
                                    />
                                </FormGrid>
                            </Grid>
                        </Grid>

                    </Box>
                    <Box>
                    <Accordion expanded title={<Typography className={classes.title} variant="body2"> {utils.string.t('claims.underWritingGroups.title')}</Typography>}>
                        <Grid container direction="row">
                            <Grid item xs={12}>
                                <ClaimsUnderwritingGroups fields={fields} claimForm={claimForm} uwResetKey={uwResetKey} hasClaimRef={hasClaimRef} />
                            </Grid>
                            <Grid item xs={12}>
                            <ClaimsMovementType
                                fields={fields}
                                claimForm={claimForm}
                                underWritingGroups={underWritingGroups}
                                enforceValueSet={true}
                            />
                            </Grid>
                        </Grid>
                    </Accordion>
                    </Box>
                </Layout>
            </div>
        </>
    );
}
