const styles = (theme) => ({
  row: {
    visibility: (props) => (props.expanded ? 'visible' : 'collapse'),
    height: (props) => (props.expanded ? 'auto' : 0),
    transition: 'height 1s ease-out;'
  },
});

export default styles;
