import React from 'react';
import PropTypes from 'prop-types';

//app
import * as utils from 'utils';
import { Button, FormContainer, FormFields, FormGrid, FormText, FormLabel, FormDate, FormActions, FormAutocompleteMui } from 'components';
import { ClaimsUploadViewSearchDocs } from 'modules';
import * as constants from 'consts';
import { useFormActions } from 'hooks';
import styles from './CreateRFI.styles';

//mui
import { makeStyles, Typography, Grid } from '@material-ui/core';

CreateRFIView.propTypes = {
  fields: PropTypes.array.isRequired,
  actions: PropTypes.array.isRequired,
  claimRef: PropTypes.string.isRequired,
  rfiDetails: PropTypes.object,
  isTaskLevel: PropTypes.bool.isRequired,
  isTaskSubmitted: PropTypes.bool,
  uploadDocumentsControl: PropTypes.bool,
  selectedTaskName: PropTypes.string,
  dmsActions: PropTypes.array.isRequired,
  formProps: PropTypes.shape({
    control: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired,
    formState: PropTypes.object.isRequired,
    handleSubmit: PropTypes.func.isRequired,
    reset: PropTypes.func.isRequired,
  }),
  handlers: PropTypes.shape({
    onClosingUploadModal: PropTypes.func,
  }),
};

export function CreateRFIView({
  actions,
  fields,
  claimRef,
  rfiDetails,
  isTaskLevel,
  isTaskSubmitted,
  uploadDocumentsControl,
  selectedTaskName,
  dmsActions,
  formProps,
  handlers,
}) {
  const classes = makeStyles(styles, { name: 'CreateRFI' })();

  const { control, errors, formState, handleSubmit, reset } = formProps;
  const { cancel, submit } = useFormActions(actions, reset);
  const { upload, skipUpload } = dmsActions;

  return (
    <div className={classes.root}>
      <FormContainer type="dialog" onSubmit={handleSubmit} data-testid="form-create-RFI">
        <FormFields type="dialog">
          <Typography variant="h5" className={classes.sectionheader}>
            {utils.string.t('claims.processing.taskFunction.RfiDetails')}
          </Typography>
          <FormGrid container spacing={2}>
            <FormGrid container alignItems="center">
              <FormGrid item xs={2}>
                <FormLabel label={utils.string.t('claims.processing.taskDetailsLabels.claimRef')} align="right" />
              </FormGrid>
              <FormGrid item xs={4}>
                <FormLabel label={claimRef} align="left" />
              </FormGrid>
              <FormGrid container alignItems="center" xs={6}>
                <FormGrid item xs={4}>
                  <FormLabel label={isTaskLevel ? `${utils.string.t('claims.columns.createRFIColumns.taskName')}` : ''} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  {isTaskLevel && <FormLabel label={selectedTaskName} align="left" />}
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={6}>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.columns.createRFIColumns.queryCode')} *`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'queryCode', control)}
                    error={errors.queryCode}
                    nestedClasses={{ root: classes.codeSelect }}
                  />
                </FormGrid>
              </FormGrid>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.columns.createRFIColumns.sendTo')} *`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'sendTo', control)}
                    error={errors.sendTo}
                    nestedClasses={{ root: classes.codeSelect }}
                  />
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid item xs={12} sm={6}>
              <FormGrid container alignItems="center">
                <FormGrid item xs={4}>
                  <FormLabel label={`${utils.string.t('claims.columns.createRFIColumns.priority')} *`} align="right" />
                </FormGrid>
                <FormGrid item xs={8}>
                  <FormAutocompleteMui
                    {...utils.form.getFieldProps(fields, 'priority', control)}
                    error={errors.priority}
                    nestedClasses={{ root: classes.codeSelect }}
                  />
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <FormGrid xs={12} sm={12}>
              <FormGrid container>
                <FormGrid item xs={2}>
                  <FormLabel label={`${utils.string.t('claims.processing.taskDetailsLabels.description')} *`} align="right" />
                </FormGrid>
                <FormGrid item xs={10}>
                  <FormText {...utils.form.getFieldProps(fields, 'description', control)} error={errors.description} />
                </FormGrid>
              </FormGrid>
            </FormGrid>
            <Typography variant="h5" className={classes.dueDateheader}>
              {utils.string.t('claims.processing.taskFunction.RfiDetails')}
            </Typography>
            <FormGrid xs={12} sm={12} alignItems="center" nestedClasses={{ root: classes.greyGrid }}>
              <FormGrid item xs={12} sm={6}>
                <FormGrid container alignItems="center">
                  <FormGrid item xs={4}>
                    <FormLabel label={utils.string.t('claims.processing.taskDetailsLabels.targetDueDate')} align="right" />
                  </FormGrid>
                  <FormGrid item xs={8}>
                    <FormDate {...utils.form.getFieldProps(fields, 'targetDueDate', control)} error={errors.targetDueDate} />
                  </FormGrid>
                </FormGrid>
              </FormGrid>
              <FormGrid item xs={12} sm={6}>
                <FormGrid container alignItems="center">
                  <FormGrid item xs={4}>
                    <FormLabel label={utils.string.t('claims.processing.taskDetailsLabels.reminder')} align="right" />
                  </FormGrid>
                  <FormGrid item xs={8}>
                    <FormAutocompleteMui
                      {...utils.form.getFieldProps(fields, 'reminder', control)}
                      nestedClasses={{ root: classes.catCodeSelect }}
                    />
                  </FormGrid>
                </FormGrid>
              </FormGrid>
            </FormGrid>
          </FormGrid>

          <FormActions type="dialog">
            {cancel && (
              <Button text={cancel.label} variant="outlined" size="medium" disabled={formState.isSubmitting} onClick={cancel.handler} />
            )}
            {submit && (
              <Button
                text={submit.label}
                type="submit"
                disabled={formState.isSubmitting || isTaskSubmitted}
                onClick={handleSubmit(submit.handler)}
                color="primary"
              />
            )}
          </FormActions>
          {isTaskSubmitted && (
            <FormGrid container>
              <FormGrid item xs={6} container alignItems="center">
                <FormLabel variant="h5" label={utils.string.t('dms.wrapper.uploadDocsPrompt')} align="left" />
              </FormGrid>
              <FormGrid item xs={6}>
                {skipUpload && <Button text={skipUpload.label} variant="outlined" size="medium" onClick={skipUpload.handler} />}
                {upload && (
                  <Button
                    text={upload.label}
                    color="primary"
                    size="medium"
                    onClick={upload.handler}
                    nestedClasses={{ btn: classes.uploadYesBtn }}
                  />
                )}
              </FormGrid>
            </FormGrid>
          )}
          {uploadDocumentsControl && (
            <Grid item xs={12}>
              <ClaimsUploadViewSearchDocs
                refData={rfiDetails}
                refIdName={constants.DMS_CONTEXT_TASK_ID}
                dmsContext={constants.DMS_CONTEXT_TASK}
                documentTypeKey={constants.DMS_DOCUMENT_TYPE_SECTION_KEYS.claims}
                handlers={{ onClosingUploadModal: handlers.onClosingUploadModal }}
              />
            </Grid>
          )}
        </FormFields>
      </FormContainer>
    </div>
  );
}
