import { ErrorMessage } from 'components';
import { withKnobs, object, text, boolean, select } from '@storybook/addon-knobs';
import { Box } from '@material-ui/core';

export default {
  title: 'ErrorMessage',
  component: ErrorMessage,
  decorators: [withKnobs],
};

export const Default = () => {
  const label = 'Error';
  const errorMsg = {
    error: 'Something went wrong',
  };
  const error = object(label, errorMsg);
  const options = ['xs', 'sm', 'md', 'lg', 'xl'];
  const defaultValue = 'xs';
  const size = select('Size', options, defaultValue);

  return (
    <Box>
      <ErrorMessage
        error={error}
        hint={text('Hint', 'Error goes here')}
        size={size}
        bold={boolean('Bold', false)}
        component={('Component', 'User')}
      />
    </Box>
  );
};
