import React, { useState } from 'react';
import PropTypes from 'prop-types';
import styles from './LossDetail.styles';
import * as utils from 'utils';
import moment from 'moment';
import { useHistory } from 'react-router';

//app
import { Layout, Accordion, TableHead, TableCell, Overflow, Skeleton, Chip } from 'components';
import { DetailCard } from 'modules';
import config from 'config';
import { STATUS_CLAIMS_DRAFT, STATUS_NOT_APPLICABLE } from 'consts';

//mui
import { makeStyles, Typography, Grid, Table, TableBody, TableRow, Divider } from '@material-ui/core';
import { Edit } from '@material-ui/icons';

LossDetailView.propTypes = {
  columns: PropTypes.array.isRequired,
  lossInfo: PropTypes.array.isRequired,
  lossRef: PropTypes.string.isRequired,
  claimsAssociateWithLoss: PropTypes.array.isRequired,
  isClaimAssociateWithLossLoading: PropTypes.bool.isRequired,
};

export default function LossDetailView({ columns, lossInfo, lossRef, claimsAssociateWithLoss, isClaimAssociateWithLossLoading }) {
  const classes = makeStyles(styles, { name: 'LossDetail' })();
  const history = useHistory();
  const [accordionsExpandStatus, setAccordiansStatus] = useState({
    [utils.string.t('loss.lossInformation.title')]: true,
    [utils.string.t('loss.relatedClaims.title')]: true,
  });

  const infoCard = (title = '', details = '', isLoading = false) => {
    return <DetailCard key={title} title={title} details={details} isLoading={isLoading} width={'22%'} />;
  };

  const renderInfoCard = (items) => {
    return <div className={classes.infoCardContainer}>{items?.map((info) => infoCard(info?.title, info?.value, info?.isLoading))}</div>;
  };

  const checkClaimStatus = utils.generic.isValidArray(claimsAssociateWithLoss, true)
    ? Boolean(claimsAssociateWithLoss?.find((claim) => claim?.claimStatus !== STATUS_CLAIMS_DRAFT?.toUpperCase()))
    : false;

  const actions = () => {
    return [
      {
        id: '1',
        icon: Edit,
        color: 'primary',
        onClick: () => {
          history.push({
            pathname: `${config.routes.claimsFNOL.newLoss}`,
            state: {
              redirectUrl: `${config.routes.claimsFNOL.loss}/${lossRef}`,
              loss: { isNextDiabled: true, isClaimSubmitted: checkClaimStatus },
            },
          });
        },
      },
    ];
  };

  const accordionTitle = (title) => {
    return (
      <Typography className={classes.title} variant="body2">
        {' '}
        {title}
      </Typography>
    );
  };

  const setAccordiansIsExpanded = (cardName, isExpand) => {
    const accordionsStatus = { ...accordionsExpandStatus };
    accordionsStatus[cardName] = isExpand;
    setAccordiansStatus(accordionsStatus);
  };

  const lossRefHandler = (event, claimRef) => {
    if (event.detail === 2) {
      history.push(`${config.routes.claimsFNOL.claim}/${claimRef}`);
    }
  };

  return (
    <div className={classes.wrapper}>
      <Layout main padding>
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.lossInformation.title')]}
          title={accordionTitle(utils.string.t('loss.lossInformation.title'))}
          actions={actions()}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.lossInformation.title'), isExpanded)}
        >
          {renderInfoCard(lossInfo)}
        </Accordion>
        <Divider />
        <Accordion
          expanded={accordionsExpandStatus[utils.string.t('loss.relatedClaims.title')]}
          title={accordionTitle(utils.string.t('loss.relatedClaims.title'))}
          onChange={(event, isExpanded) => setAccordiansIsExpanded(utils.string.t('loss.relatedClaims.title'), isExpanded)}
        >
          <Grid item xs={12}>
            <Grid container direction="column">
              <Overflow>
                <Table>
                  <TableHead columns={columns} />
                  <TableBody>
                    {isClaimAssociateWithLossLoading ? (
                      <TableRow>
                        <TableCell colSpan={columns?.length}>
                          <Skeleton height={40} animation="wave" displayNumber={3} />
                        </TableCell>
                      </TableRow>
                    ) : (
                      utils.generic.isValidArray(claimsAssociateWithLoss, true) &&
                      claimsAssociateWithLoss?.map((claim, index) => (
                        <TableRow key={index} onClick={(e) => lossRefHandler(e, claim?.claimReference)}>
                          <TableCell>
                            {claim?.claimReference || STATUS_NOT_APPLICABLE}
                            <Chip type={'new'} nestedClasses={{ root: classes.inFlightMarks }} />
                          </TableCell>
                          <TableCell>{claim?.claimant || STATUS_NOT_APPLICABLE}</TableCell>
                          <TableCell>{claim?.policyRef || STATUS_NOT_APPLICABLE}</TableCell>
                          <TableCell>
                            {moment(claim?.claimReceivedDate).format(config.ui.format.date.text) || STATUS_NOT_APPLICABLE}
                          </TableCell>
                          <TableCell>{claim?.division || STATUS_NOT_APPLICABLE}</TableCell>
                          <TableCell>{claim?.insured || STATUS_NOT_APPLICABLE}</TableCell>
                          <TableCell>{claim?.riskDetails || STATUS_NOT_APPLICABLE}</TableCell>
                          <TableCell>{claim?.claimStatus || STATUS_NOT_APPLICABLE}</TableCell>
                        </TableRow>
                      ))
                    )}
                  </TableBody>
                </Table>
              </Overflow>
            </Grid>
          </Grid>
        </Accordion>
        <div>
          <Chip label="" type={'new'} nestedClasses={{ root: classes.inFlightMarksText }} />{' '}
          {utils.string.t('loss.relatedClaims.inFlightMarksText')}
        </div>
      </Layout>
    </div>
  );
}
