import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import PropTypes from 'prop-types';

//app
import styles from './DrawerComponent.styles';
import { setDmsWidgetExpand } from 'stores';

//mui
import { makeStyles, Drawer, Divider, IconButton } from '@material-ui/core';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import DescriptionIcon from '@material-ui/icons/Description';

DrawerComponent.propTypes = {
  isDrawerOpen: PropTypes.bool.isRequired,
};

export default function DrawerComponent({ isDrawerOpen, children }) {
  const nestedClasses = makeStyles(styles, { name: 'DrawerComponent' })();
  const dispatch = useDispatch();
  const [isDrawerExpanded, setIsDrawerExpanded] = useState(false);

  const handleArrowClick = () => {
    setIsDrawerExpanded(!isDrawerExpanded);
  };

  useEffect(() => {
    dispatch(setDmsWidgetExpand(isDrawerExpanded));
  }, [isDrawerExpanded]); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <Drawer
      variant="persistent"
      anchor="right"
      open={isDrawerOpen}
      classes={{
        paper: isDrawerExpanded ? nestedClasses.drawerPaperExpanded : nestedClasses.drawerPaper,
      }}
    >
      <IconButton color="inherit" aria-label="open" edge="start" onClick={handleArrowClick}>
        {isDrawerExpanded ? <ChevronRightIcon /> : <ChevronLeftIcon />}
        {isDrawerExpanded ? null : <DescriptionIcon />}
      </IconButton>
      <Divider />
      {children}
    </Drawer>
  );
}
