import React from 'react';
import PropTypes from 'prop-types';

//app
import { Empty } from 'components';
import * as utils from 'utils';
import * as constants from 'consts';
import { ReactComponent as IconSearchFile } from '../../assets/svg/line-icon-search-file.svg';

//mui
import { Box } from '@material-ui/core';

DmsDocViewerView.propTypes = {
  url: PropTypes.string.isRequired,
  fileExt: PropTypes.string.isRequired,
};

export function DmsDocViewerView({ url, fileExt }) {

  const renderDocFrame = (url) => {
    const isBlacklisted = constants.DMS_DOC_VIEW_FORBIDDEN_FORMATS.includes(fileExt);
    let style = { border: '1px solid grey', width: '100%', height: '700px' };
    switch (fileExt) {
      case 'pdf':
      case 'txt':
      case 'rtf':
        style = { border: '1px solid grey', width: '100%', height: '700px' };
        break;
      case 'mp4':
      case 'mp3':
      case 'mov':
        style = { border: '1px solid grey', width: '100%', height: '400px' };
        break;
      case 'doc':
      case 'docx':
      case 'xls':
      case 'xlsx':
      case 'ppt':
      case 'pptx':
        style = { border: '1px solid grey', width: '2%', height: '2%' };
        break;
      default:
        style = { border: '1px solid grey', width: '100%', height: '700px' };
    }
    return (
      !isBlacklisted ?
        (<iframe src={url}
          title="iframe1" name="iframe1" id="iframe1"
          frameborder="0" border="0" cellspacing="0"
          style={style}
        />)
        :
        (
          <>
            <iframe src={url}
              title="iframe1" name="iframe1" id="iframe1"
              frameborder="0" border="0" cellspacing="0"
              style={style}
            />
            <Empty
              title={utils.string.t('dms.docViewer.noRender')}
              text={utils.string.t('dms.docViewer.noRender')}
              icon={<IconSearchFile />}
              padding
            />
          </>
        )
    )
  }

  return (
    <Box>
      {url && renderDocFrame(url)}

      {!url && (
        <Empty
          title={utils.string.t('dms.docViewer.noRender')}
          text={utils.string.t('dms.docViewer.noRender')}
          icon={<IconSearchFile />}
          padding
        />
      )}
    </Box>
  );
}
